DROP TABLE IF EXISTS clients;
CREATE TABLE clients (id UUID NOT NULL, client_name CHARACTER VARYING(250) NOT NULL, client_lastname CHARACTER VARYING(250) NOT NULL, client_address CHARACTER VARYING(1000) NOT NULL, PRIMARY KEY (id));
DROP TABLE IF EXISTS countries;
CREATE TABLE countries (id UUID NOT NULL, country_name CHARACTER VARYING(250) NOT NULL, country_code CHARACTER VARYING(10) NOT NULL, country_alias CHARACTER VARYING(250) NOT NULL, PRIMARY KEY (id), UNIQUE (id));
DROP TABLE IF EXISTS order_shippings;
CREATE TABLE order_shippings (id UUID NOT NULL, notes CHARACTER VARYING(1000), id_client UUID NOT NULL, id_order UUID NOT NULL, PRIMARY KEY (id));
DROP TABLE IF EXISTS orders;
CREATE TABLE orders (id UUID NOT NULL, id_country UUID NOT NULL, id_store UUID NOT NULL, creation_date TIMESTAMP(6) WITHOUT TIME ZONE NOT NULL, payment_status CHARACTER VARYING, PRIMARY KEY (id));
DROP TABLE IF EXISTS product_categories;
CREATE TABLE product_categories (id UUID NOT NULL, category_name CHARACTER VARYING(100) NOT NULL, CONSTRAINT product_category_pkey PRIMARY KEY (id));
DROP TABLE IF EXISTS product_sub_categories;
CREATE TABLE product_sub_categories (id UUID NOT NULL, sub_category_name CHARACTER VARYING(100) NOT NULL, id_category UUID NOT NULL, CONSTRAINT product_sub_category_pkey PRIMARY KEY (id));
DROP TABLE IF EXISTS products;
CREATE TABLE products (id UUID NOT NULL, product_name CHARACTER VARYING(500) NOT NULL, weight_unit CHARACTER VARYING(250) NOT NULL, product_description CHARACTER VARYING(1000), price DOUBLE PRECISION NOT NULL, weight DOUBLE PRECISION NOT NULL, id_product_sub_category UUID DEFAULT 'ffdabf28-1917-4ee1-b7cf-219199c6cc75'::uuid NOT NULL, id_store UUID DEFAULT '545a5ca8-6df3-11ee-b962-0242ac120002'::uuid NOT NULL, PRIMARY KEY (id));
DROP TABLE IF EXISTS shipping_articles;
CREATE TABLE shipping_articles (id UUID NOT NULL, article_notes CHARACTER VARYING(1000), amount INTEGER NOT NULL, id_product UUID NOT NULL, id_shipping UUID NOT NULL, PRIMARY KEY (id));
DROP TABLE IF EXISTS stores;
CREATE TABLE stores (id UUID NOT NULL, store_name CHARACTER VARYING(250) NOT NULL, store_code CHARACTER VARYING(250) NOT NULL, PRIMARY KEY (id));
DROP TABLE IF EXISTS users;
CREATE TABLE users (id UUID NOT NULL, username CHARACTER VARYING(250) NOT NULL, user_password CHARACTER VARYING(100) NOT NULL, PRIMARY KEY (id), UNIQUE (username), UNIQUE (id));
ALTER TABLE "order_shippings" ADD CONSTRAINT order_shippings_clients_id_fk FOREIGN KEY ("id_client") REFERENCES "clients" ("id") ON DELETE CASCADE ON UPDATE CASCADE;
ALTER TABLE "order_shippings" ADD CONSTRAINT order_shippings_orders_id_fk FOREIGN KEY ("id_order") REFERENCES "orders" ("id") ON DELETE CASCADE ON UPDATE CASCADE;
ALTER TABLE "orders" ADD CONSTRAINT orders_countries_id_fk FOREIGN KEY ("id_country") REFERENCES "countries" ("id") ON DELETE CASCADE ON UPDATE CASCADE;
ALTER TABLE "orders" ADD CONSTRAINT orders_stores_id_fk FOREIGN KEY ("id_store") REFERENCES "stores" ("id") ON DELETE CASCADE ON UPDATE CASCADE;
ALTER TABLE "orders" ADD CONSTRAINT orders_payment_status CHECK ((payment_status)::text = ANY ((ARRAY['pending'::character varying, 'done'::character varying])::text[]));
ALTER TABLE "product_sub_categories" ADD CONSTRAINT product_sub_category_product_category_id_fk FOREIGN KEY ("id_category") REFERENCES "product_categories" ("id");
ALTER TABLE "shipping_articles" ADD CONSTRAINT shipping_articles_products_id_fk FOREIGN KEY ("id_product") REFERENCES "products" ("id");
ALTER TABLE "shipping_articles" ADD CONSTRAINT shipping_articles_order_shippings_id_fk FOREIGN KEY ("id_shipping") REFERENCES "order_shippings" ("id");
